mapper.select:
SELECT A.*, M.name AS writerName FROM article AS A INNER JOIN `member` AS M ON A.memberId = M.Id WHERE 1 = 1
AND boardId = #{boardId}
ORDER BY id DESC

mapper.update:
UPDATE article
		SET updateDate = NOW(),

			title = #{title},


		`body` = #{body}

	WHERE id = #{id}

mapper.select:
SELECT COUNT(*) 
	FROM article
	WHERE boardId = #{boardId};
